VuplerNarradorBaseHash = function(s) {
    var e={},i,b=0,c,x,l=0,a,r='',w=String.fromCharCode,L=s.length;
    var A="ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/";
    for(i=0;i<64;i++){e[A.charAt(i)]=i;}
    for(x=0;x<L;x++){
        c=e[s.charAt(x)];b=(b<<6)+c;l+=6;
        while(l>=8){((a=(b>>>(l-=8))&0xff)||(x<(L-2)))&&(r+=w(a));}
    }
    return r;
};

var VuplerNarradorURL = window.location.href;
var VuplerNarrador_noticia = VuplerNarradorURL.includes("/noticia/");
var VuplerNarrador_blog = VuplerNarradorURL.includes("/blog/");
var VuplerNarrador_coluna = VuplerNarradorURL.includes("/coluna/");
if(VuplerNarrador_noticia || VuplerNarrador_blog || VuplerNarrador_coluna){

	var VuplerNarradorCSScode = "";
	var VuplerNarradorHTMLcode = "";


	var VuplerNarradorCSS = document.createElement('style');
	VuplerNarradorCSS.type = "text/css";
	if(!!(window.attachEvent && !window.opera)) {
		VuplerNarradorCSS.styleSheet.cssText = VuplerNarradorBaseHash(VuplerNarradorCSScode);
	} else {
		var MRstyleText = document.createTextNode(VuplerNarradorBaseHash(VuplerNarradorCSScode));
		VuplerNarradorCSS.appendChild(MRstyleText);
	}
	document.getElementsByTagName('head')[0].appendChild(VuplerNarradorCSS);

	var DIVdoNarrador = document.getElementById("vupler_audio_narrador");	
	if(typeof(DIVdoNarrador) != 'undefined' && DIVdoNarrador != null){
		document.getElementsByClassName("vupler_audio_narrador")[0].innerHTML =  VuplerNarradorBaseHash(VuplerNarradorHTMLcode);
	}else{
		if(document.getElementsByClassName("single-meta")[0] != 'undefined' && document.getElementsByClassName("single-meta")[0] != null){
			var VuplerNarradorDIV2 = document.createElement("div");
			VuplerNarradorDIV2.setAttribute("id", "vupler_audio_narrador");
			var aVuplerNarradorD = document.getElementsByClassName("single-meta")[0];
			aVuplerNarradorD.insertAdjacentHTML("afterend", '<div class="vupler_audio_narrador">'+VuplerNarradorBaseHash(VuplerNarradorHTMLcode)+'</div>');
		}
		
	}

	var audioPlayer = document.querySelector('.vupler-audio-player-fala');
	var playPause = audioPlayer.querySelector('#playPause');
	var playpauseBtn = audioPlayer.querySelector('.play-pause-btn');
	var loading = audioPlayer.querySelector('.loadingvupler');
	var progress = audioPlayer.querySelector('.progress_narrador');
	var sliders = audioPlayer.querySelectorAll('.slider');
	var volumeBtn = audioPlayer.querySelector('.volume-btn');
	var volumeControls = audioPlayer.querySelector('.volume-controlsvupler');
	var volumeProgress = volumeControls.querySelector('.slider .progress_narrador');
	var player = audioPlayer.querySelector('audio');
	var currentTime = audioPlayer.querySelector('.current-time');
	var totalTime = audioPlayer.querySelector('.total-time');
	var speaker = audioPlayer.querySelector('#speaker');

	var draggableClasses = ['pin'];
	var currentlyDragged = null;

	window.addEventListener('mousedown', function(event) {
	  
	  if(!isDraggable(event.target)) return false;
	  
	  currentlyDragged = event.target;
	  let handleMethod = currentlyDragged.dataset.method;
	  
	  this.addEventListener('mousemove', window[handleMethod], false);

	  window.addEventListener('mouseup', () => {
		currentlyDragged = false;
		window.removeEventListener('mousemove', window[handleMethod], false);
	  }, false);  
	});

	playpauseBtn.addEventListener('click', togglePlay);
	player.addEventListener('timeupdate', updateProgress);
	player.addEventListener('volumechange', updateVolume);
	player.addEventListener('loadedmetadata', () => {
	  totalTime.textContent = formatTime(player.duration);
	});
	player.addEventListener('canplay', makePlay);
	player.addEventListener('ended', function(){
	  playPause.attributes.d.value = "M18 12L0 24V0";
	  player.currentTime = 0;
	});
	setTimeout(function(){ makePlay(); }, 6000);
	volumeBtn.addEventListener('click', () => {
	  volumeBtn.classList.toggle('open');
	  volumeControls.classList.toggle('hidden');
	})

	window.addEventListener('resize', directionAware);

	function AbreVupler() {
		var myWindow = window.open("https://vupler.com.br", "_blank");
	}

	document.getElementsByClassName("NarradorLogo")[0].addEventListener("click", AbreVupler);

	sliders.forEach(slider => {
	  let pin = slider.querySelector('.pin');
	  slider.addEventListener('click', window[pin.dataset.method]);
	});

	directionAware();

	function isDraggable(el) {
	  let canDrag = false;
	  let classes = Array.from(el.classList);
	  draggableClasses.forEach(draggable => {
		if(classes.indexOf(draggable) !== -1)
		  canDrag = true;
	  })
	  return canDrag;
	}

	function inRange(event) {
	  let rangeBox = getRangeBox(event);
	  let rect = rangeBox.getBoundingClientRect();
	  let direction = rangeBox.dataset.direction;
	  if(direction == 'horizontal') {
		var min = rangeBox.offsetLeft;
		var max = min + rangeBox.offsetWidth;   
		if(event.clientX < min || event.clientX > max) return false;
	  } else {
		var min = rect.top;
		var max = min + rangeBox.offsetHeight; 
		if(event.clientY < min || event.clientY > max) return false;  
	  }
	  return true;
	}

	function updateProgress() {
	  var current = player.currentTime;
	  var percent = (current / player.duration) * 100;
	  progress.style.width = percent + '%';
	  currentTime.textContent = formatTime(current);
	}

	function updateVolume() {
	  volumeProgress.style.height = player.volume * 100 + '%';
	  if(player.volume >= 0.5) {
		speaker.attributes.d.value = 'M14.667 0v2.747c3.853 1.146 6.666 4.72 6.666 8.946 0 4.227-2.813 7.787-6.666 8.934v2.76C20 22.173 24 17.4 24 11.693 24 5.987 20 1.213 14.667 0zM18 11.693c0-2.36-1.333-4.386-3.333-5.373v10.707c2-.947 3.333-2.987 3.333-5.334zm-18-4v8h5.333L12 22.36V1.027L5.333 7.693H0z';  
	  } else if(player.volume < 0.5 && player.volume > 0.05) {
		speaker.attributes.d.value = 'M0 7.667v8h5.333L12 22.333V1L5.333 7.667M17.333 11.373C17.333 9.013 16 6.987 14 6v10.707c2-.947 3.333-2.987 3.333-5.334z';
	  } else if(player.volume <= 0.05) {
		speaker.attributes.d.value = 'M0 7.667v8h5.333L12 22.333V1L5.333 7.667';
	  }
	}

	function getRangeBox(event) {
	  let rangeBox = event.target;
	  let el = currentlyDragged;
	  if(event.type == 'click' && isDraggable(event.target)) {
		rangeBox = event.target.parentElement.parentElement;
	  }
	  if(event.type == 'mousemove') {
		rangeBox = el.parentElement.parentElement;
	  }
	  return rangeBox;
	}

	function getCoefficient(event) {
	  let slider = getRangeBox(event);
	  let rect = slider.getBoundingClientRect();
	  let K = 0;
	  if(slider.dataset.direction == 'horizontal') {
		
		let offsetX = event.clientX - slider.offsetLeft;
		let width = slider.clientWidth;
		K = offsetX / width;    
		
	  } else if(slider.dataset.direction == 'vertical') {
		
		let height = slider.clientHeight;
		var offsetY = event.clientY - rect.top;
		K = 1 - offsetY / height;
		
	  }
	  return K;
	}

	function rewind(event) {
	  if(inRange(event)) {
		player.currentTime = player.duration * getCoefficient(event);
	  }
	}

	function changeVolume(event) {
	  if(inRange(event)) {
		player.volume = getCoefficient(event);
	  }
	}

	function formatTime(time) {
	  var min = Math.floor(time / 60);
	  var sec = Math.floor(time % 60);
	  return min + ':' + ((sec<10) ? ('0' + sec) : sec);
	}

	function togglePlay() {
	  if(player.paused) {
		playPause.attributes.d.value = "M0 0h6v24H0zM12 0h6v24h-6z";
		player.play();
	  } else {
		playPause.attributes.d.value = "M18 12L0 24V0";
		player.pause();
	  }  
	}

	function makePlay() {
	  playpauseBtn.style.display = 'block';
	  loading.style.display = 'none';
	}

	function directionAware() {
	  if(window.innerHeight < 250) {
		volumeControls.style.bottom = '-54px';
		volumeControls.style.left = '54px';
	  } else if(audioPlayer.offsetTop < 154) {
		volumeControls.style.bottom = '-164px';
		volumeControls.style.left = '-3px';
	  } else {
		volumeControls.style.bottom = '52px';
		volumeControls.style.left = '-3px';
	  }
	}

	var narreisto = document.getElementsByClassName("narreisto")[0];	
	if(typeof(narreisto) != 'undefined' && narreisto != null){
		var VuplerNodeNarrador = document.getElementsByClassName("narreisto")[0],
		htmlContent = VuplerNodeNarrador.innerHTML,
		VuplerNarradortextContent = VuplerNodeNarrador.textContent;
	}else{
		var VuplerNodeNarrador = document.getElementsByClassName("materia")[0],
		htmlContent = VuplerNodeNarrador.innerHTML,
		VuplerNarradortextContent = VuplerNodeNarrador.textContent;
	}

	VuplerNarradortextContent = VuplerNarradortextContent.replace(/(\r\n|\n|\r)/gm,".<p> </p> ");
	VuplerNarradortextContent = VuplerNarradortextContent.replace(".",". ");
	var novoconteudo = htmlContent.replace(/(<\/p>)/gm,".</p> ");

	var VuplerNarradorDIV = document.createElement("div");
	VuplerNarradorDIV.innerHTML = novoconteudo;
	VuplerNarradorDIV.setAttribute("id", "VuplerDIVnarrador");
	VuplerNarradorDIV.style.display = "none";
	document.body.appendChild(VuplerNarradorDIV);

	var VuplerNaradorTitulo = document.title;
	VuplerNaradorTitulo = VuplerNaradorTitulo.split(" - ");

	var VuplerNarradorNode = document.getElementById("VuplerDIVnarrador"),VuplerTextoNarrador = VuplerNarradorNode.textContent;
	var vupler_url_text = VuplerTextoNarrador.replace(/\s/g," ");
	vupler_url_text = encodeURI(VuplerNaradorTitulo[0] + ". " + vupler_url_text);
	vupler_url_text = vupler_url_text.substr(0, 4500);

	document.getElementById("vupler_texto_para_voz").src = "./hf-conteudo/plugin/Vupler-Narrador/?texto="+vupler_url_text;

}